using ArraysAndStrings;

namespace Tests;

public class TwoPointersServiceTests
{
    private readonly ITwoPointersService _twoPointersService;

    public TwoPointersServiceTests()
    {
        _twoPointersService = new TwoPointersService();
    }

    
    [Fact]
    public void IsPalindrome_Example1_Success()
    {
        // Arrange
        string s = "ab_a";

        // Act
        var result = _twoPointersService.IsPalindrome(s);

        // Assert
        Assert.True(result);
    }

    [Fact]
    public void IsSubsequence_Example1_Success()
    {
        // Arrange
        string s = "abc";
        string t = "ahbgdc";

        // Act
        var result = _twoPointersService.IsSubsequence(s, t);

        // Assert
        Assert.True(result);
    }

    [Fact]
    public void IsSubsequence_Example2_Success()
    {
        // Arrange
        string s = "bcd";
        string t = "";

        // Act
        var result = _twoPointersService.IsSubsequence(s, t);

        // Assert
        Assert.True(result);
    }
}